private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
    res2placas.setText("");
    int i = 1;
    double a, lim;
    res2placas.setText("");
    a = Double.parseDouble(v2a.getText());
    lim = Double.parseDouble(f2c.getText());
    lim *= 1000000000;
    a /= 100;
    oper num = new oper();
    oper lamb = new oper();
    oper redo = new oper();
    do {
        num.fc2placas(i, a);
        lamb.lam2placas(lim, num.fc);
        if (num.fc < lim) {
            if ("".equals(res2placas.getText())) {
                res2placas.append("fc (" + i+")" + " = " + (num.fc) / 1000000000 + " Ghz " + "   " + " Lambda" + i + " = " + (redo.Redondear(lamb.lmb)) * 100 + " cms");
                i++;
            } else {
                 res2placas.append("\nfc (" + i+")" + " = " + (num.fc) / 1000000000 + " Ghz " + "   " + " Lambda" + i + " = " + (redo.Redondear(lamb.lmb)) * 100 + " cms");
                i++; 
            }
        }
    } while (num.fc < lim);
}//GEN-LAST:event_jButton1ActionPerformed

