private void calcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_calcularActionPerformed
// TODO add your handling code here:
    if(sep.isSelected())
    {
        mas= mas+33;
        barrita.setValue(mas);
    }
    if(zr.isSelected())
    {
        mas= mas+33;
        barrita.setValue(mas);
    }
    if(ame.isSelected())
    {
        mas= mas+33;
        barrita.setValue(mas);
    }
    if(barrita.getValue()==99)    
    {
        mas= mas+1;
        barrita.setValue(mas);
        
    }
    JOptionPane.showConfirmDialog(rootPane,"tu porcentaje es" + mas + "%", null, WIDTH);
    mas=0;
    barrita.setValue(0);
}//GEN-LAST:event_calcularActionPerformed

