private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
// TODO add your handling code here:
    String c = app.getText();
    String u = apm.getText();
    String r = nom.getText();
    String p = entidad.getText();
    
    String a = a1.getText();
    String m = mes.getText();
    String d = dia.getText();
    String g = gene.getText();
    
    
    curp.leemos(c, u, r, p);
    curp.leem(g, a, m, d);
    
    app1.setText(curp.curp()+ curp.dos() + curp.cuatro() + curp.tres() + "09");
    
}//GEN-LAST:event_jButton1ActionPerformed

