private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
// TODO add your handling code here:
    vars = Double.parseDouble(sul.getText());
    varc = Double.parseDouble(car.getText());
    varh = Double.parseDouble(hid.getText());
    varo = Double.parseDouble(ox.getText());
    hv = (145*(varc)) + (620*((varh - varo)/8)) + 40*(vars); 
    resto.setText(String.valueOf(hv));
}//GEN-LAST:event_jButton2ActionPerformed

