private void jButton8ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton8ActionPerformed
// TODO add your handling code here:
    double T1,P1,P2,M,wo,no, logi, te,www, pres;
    
    NumberFormat nf = NumberFormat.getInstance();
    no = Double.parseDouble(n.getText());
    P1 = Double.parseDouble(p1.getText());
    P2 = Double.parseDouble(p2.getText());
    T1 = Double.parseDouble(t1.getText());
    M = Double.parseDouble(m.getText());
    wo = Double.parseDouble(ww.getText());
    pres= P1/P2;
    logi= (no-1)/no;
    
     nf.setMaximumFractionDigits(2);
    te = T1*Math.pow(pres,logi);
    www= (wo*1545.3*T1)/((M*(no-1))*(1-Math.pow(pres, logi)));
    String f = nf.format(te);
    String oe = nf.format(www); 
    res.setText(String.valueOf(f) +" R");
    w.setText(String.valueOf(oe)+ " lb");
    
}//GEN-LAST:event_jButton8ActionPerformed

