private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
// TODO add your handling code here:
    double x=0, y=0;
    double res=0, ress=0;
    double vax1,vax2, rex;
    double vay1,vay2,rey;
    double errorr, errora;
    double jex= Double.parseDouble(vaxx.getText());
    double jey= Double.parseDouble(vayy.getText());
    int g=0;
    
    errorr=Double.parseDouble(error.getText());
    
    vax1= Double.parseDouble(matriz.getValueAt(0, 0).toString());
    vax2= Double.parseDouble(matriz.getValueAt(0, 1).toString());
    rex= Double.parseDouble(matriz.getValueAt(0, 2).toString());
    
    vay1=Double.parseDouble(matriz.getValueAt(1, 0).toString());
    vay2=Double.parseDouble(matriz.getValueAt(1, 1).toString());
    rey=Double.parseDouble(matriz.getValueAt(1, 2).toString());   
    
   
    errora=100;
    rezo.setValueAt(0, 0, 0);
    rezo.setValueAt(jex, 0, 1);
    rezo.setValueAt(jey, 0, 2);
    rezo.setValueAt(errora, 0, 3);
    rezo.setValueAt(errora, 0, 4);
   
    
    
    for(g=1;errora>=errorr;g++)
    {
    x=(rex-(vax2*jey))/vax1;
    y=(rey-(vay1*x))/vay2;
    res=(x-jex)/x;
    ress=(y-jey)/y;
    jey=y;
    jex=x;
    if(res<0)
    {res=res*(-100);}
    else{res=res*100;}
    if(ress<0)
    {ress=ress*(-100);}
    else{ress=ress*100;}
    if(res>=ress)
    {
    errora=res;
    }
    else{errora=ress;}
    
    
    rezo.setValueAt(g, g, 0);
    rezo.setValueAt(jex, g, 1);
    rezo.setValueAt(jey, g, 2);
    rezo.setValueAt(res, g, 3);
    rezo.setValueAt(ress, g, 4);
    
    }
    
    
}//GEN-LAST:event_jButton1ActionPerformed

