private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
// TODO add your handling code here:
    double x=0, y=0, z=0;
    double res=0, ress=0, resss=0;
    double vax1,vax2, vax3, rex;
    double vay1,vay2, vay3,rey;
    double vaz1, vaz2, vaz3,rez;
    double errorr, errora;
    double jex= Double.parseDouble(vaxx.getText());
    double jey= Double.parseDouble(vayy.getText());
    double jez= Double.parseDouble(vazz.getText());
    int g=0;
    
    errorr=Double.parseDouble(error.getText());
    
    vax1= Double.parseDouble(matriz.getValueAt(0, 0).toString());
    vax2= Double.parseDouble(matriz.getValueAt(0, 1).toString());
    vax3= Double.parseDouble(matriz.getValueAt(0, 2).toString());
    rex= Double.parseDouble(matriz.getValueAt(0, 3).toString());
    
    vay1=Double.parseDouble(matriz.getValueAt(1, 0).toString());
    vay2=Double.parseDouble(matriz.getValueAt(1, 1).toString());
    vay3=Double.parseDouble(matriz.getValueAt(1, 2).toString());
    rey= Double.parseDouble(matriz.getValueAt(1, 3).toString());
    
    vaz1=Double.parseDouble(matriz.getValueAt(2, 0).toString());
    vaz2=Double.parseDouble(matriz.getValueAt(2, 1).toString());
    vaz3= Double.parseDouble(matriz.getValueAt(2, 2).toString());
    rez=Double.parseDouble(matriz.getValueAt(2, 3).toString());
    
    errora=100;
    
    rezo.setValueAt(0, 0, 0);
    rezo.setValueAt(jex, 0, 1);
    rezo.setValueAt(jey, 0, 2);
    rezo.setValueAt(jez, 0, 3);
    rezo.setValueAt(errora, 0, 4);
    rezo.setValueAt(errora, 0, 5);
    rezo.setValueAt(errora, 0, 6);
    
    
    
    for(g=1;errorr<=errora;g++)
    {
    x=(rex-(vax2*jey)-(vax3*jez))/vax1;
    y=(rey-(vay1*x)-(vay3*jez))/vay2;
    z=(rez-(vaz1*x)-(vaz2*y))/vaz3;
    
    res=(x-jex)/x;
    ress=(y-jey)/y;
    resss=(z-jez)/z;
    
    if(res<0)
    {
    res=res*(-100);
    }
    else
    {
    res=res*100;
    }
    if(ress<0)
    {
    ress=ress*(-100);
    }
    else
    {
    ress=ress*100;
    }
    if(resss<0)
    {
    resss=resss*(-100);
    }
    else
    {
    resss=resss*100;
    }
    
    if(res>=ress)
    {
        if(res>=resss)
        {
        errora=res;
        }
    }
    else
    {
    if(ress>=res)
    {
        if(ress>=resss)
        {
        errora=ress;        
        }
    }
    else
    {
        if(resss>=res)
        {
        if(resss>=ress)
        {errora=resss;}
    
        }
    }
    }
    
    
    
    rezo.setValueAt(g, g, 0);
    rezo.setValueAt(x, g, 1);
    rezo.setValueAt(y, g, 2);
    rezo.setValueAt(z, g, 3);
    rezo.setValueAt(res, g, 4);
    rezo.setValueAt(ress, g, 5);
    rezo.setValueAt(resss, g, 6);
    
    jex=x;
    jey=y;
    jez=z;
    
    
    }
         
    
}//GEN-LAST:event_jButton1ActionPerformed

