private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
// TODO add your handling code here:
    double u=0, p=0, o=0;
    double m=0,a=0,b=0;
    double ecua=0;
    double fxo=0, fx1=0;
    double bo,bi,xi,xo;
     p= Double.parseDouble(encuentra.getText());
     int varr= Integer.parseInt(var.getText());
   
    if(linea.isSelected()==true)
    {
    for(int i=0; i<=varr;i++)
    {
    u= Double.parseDouble(func.getValueAt(i, 0).toString());
    o= Double.parseDouble(func.getValueAt(i+1, 0).toString());
    m= (u+o)/2;
    if(m<=0)
    {
    if(m<=p && m>=o && m<=u)
    {
    a= Double.parseDouble(func.getValueAt(i, 1).toString());
    b= Double.parseDouble(func.getValueAt(i+1, 1).toString());
    break; 
    }
    }
    if(m>=p && m<=o && m>=u)
    {
    a= Double.parseDouble(func.getValueAt(i, 1).toString());
    b= Double.parseDouble(func.getValueAt(i+1, 1).toString());
    break; 
    }
    }
    
    fxo=b;
    fx1=a;
    xi=u;
    xo=o;
    
    bo=fxo;
    bi= (fx1-fxo)/(xi-xo);
    ecua= bo+(bi*(p-xo));
    resultado.setText(String.valueOf(ecua));
    }
//----------------------------------------------------
    if(cuadra.isSelected()==true)
    {
    double e=0,c=0;
    double fx2=0, x2=0, b2=0;
    for(int i=0; i<=varr;i++)
    {  
    u= Double.parseDouble(func.getValueAt(i, 0).toString());
    o= Double.parseDouble(func.getValueAt(i+1, 0).toString());
    m= (u+o)/2;
  
    if(m<=0)
    {
    if(m<=p && m>=o && m<=u)
    {
    a= Double.parseDouble(func.getValueAt(i, 1).toString());
    b= Double.parseDouble(func.getValueAt(i+1, 1).toString());
        
    if(i==0)
    { 
    e= Double.parseDouble(func.getValueAt(i+2, 0).toString());
    c= Double.parseDouble(func.getValueAt(i+2, 1).toString());
    }
    else
    {
      e= Double.parseDouble(func.getValueAt(i-1, 0).toString());   
      c= Double.parseDouble(func.getValueAt(i-1, 1).toString());  
    }
    break; 
    }
    
    }   
    if(m>=p && m<=o && m>=u)
    {
    a= Double.parseDouble(func.getValueAt(i, 1).toString());
    b= Double.parseDouble(func.getValueAt(i+1, 1).toString());
        
    if(i==0)
    { 
    e= Double.parseDouble(func.getValueAt(i+2, 0).toString());
    c= Double.parseDouble(func.getValueAt(i+2, 1).toString());
    }
    else
    {
      e= Double.parseDouble(func.getValueAt(i-1, 0).toString());   
      c= Double.parseDouble(func.getValueAt(i-1, 1).toString());  
    }
    break; 
    }
    }
    
    fxo= c;
    fx1= a;
    fx2= b;
    xo= e;
    xi=u;
    x2= o;
    
    bo=fxo;
    bi= (fx1-fxo)/(xi-xo);
    b2= (((fx2-fx1)/(x2-xi))-((fx1-fxo)/(xi-xo)))/(x2-xo);
    ecua= bo+(bi*(p-xo))+(b2*(p-xi)*(p-xo));
    resultado1.setText(String.valueOf(ecua));  
    }
    
    if(cubito.isSelected()==true)
    {
      double e=0,c=0,ii=0, d=0, b3=0;
      double fx2=0, x2=0, fx3=0,x3=0;
      double b2=0,ec=0,ec1=0,ec2=0,ec3=0;
        
    for(int i=0; i<=varr;i++)
    {  
    u= Double.parseDouble(func.getValueAt(i, 0).toString());
    o= Double.parseDouble(func.getValueAt(i+1, 0).toString());
    m= (u+o)/2;
    if(m<=0)
    {
     if(m<=p && m>=o && m<=u)
    {
    a= Double.parseDouble(func.getValueAt(i, 1).toString());
    b= Double.parseDouble(func.getValueAt(i+1, 1).toString());    
    if(i==0)
    { 
    e= Double.parseDouble(func.getValueAt(i+2, 0).toString());
    c= Double.parseDouble(func.getValueAt(i+2, 1).toString());
    ii= Double.parseDouble(func.getValueAt(i+3, 0).toString());
    d= Double.parseDouble(func.getValueAt(i+3, 1).toString());
    }
    else
    {
      e= Double.parseDouble(func.getValueAt(i-1, 0).toString());   
      c= Double.parseDouble(func.getValueAt(i-1, 1).toString());
      ii= Double.parseDouble(func.getValueAt(i+2, 0).toString());
      d= Double.parseDouble(func.getValueAt(i+2, 1).toString());
    }
    break; 
    }
    
    }
    
    if(m>=p && m<=o && m>=u)
    {
    a= Double.parseDouble(func.getValueAt(i, 1).toString());
    b= Double.parseDouble(func.getValueAt(i+1, 1).toString());    
    if(i==0)
    { 
    e= Double.parseDouble(func.getValueAt(i+2, 0).toString());
    c= Double.parseDouble(func.getValueAt(i+2, 1).toString());
    ii= Double.parseDouble(func.getValueAt(i+3, 0).toString());
    d= Double.parseDouble(func.getValueAt(i+3, 1).toString());
    }
    else
    {
      e= Double.parseDouble(func.getValueAt(i-1, 0).toString());   
      c= Double.parseDouble(func.getValueAt(i-1, 1).toString());
      ii= Double.parseDouble(func.getValueAt(i+2, 0).toString());
      d= Double.parseDouble(func.getValueAt(i+2, 1).toString());
    }
    break; 
    }
    }
    fxo= c;
    fx1= a;
    fx2= b;
    fx3= d;
    xo= e;
    xi=u;
    x2= o;
    x3= ii;

    bo=fxo;
    bi= (fx1-fxo)/(xi-xo);
    b2= (((fx2-fx1)/(x2-xi))-((fx1-fxo)/(xi-xo)))/(x2-xo);

    ec=  (fx1-fx2)/(xi-x2);
    ec1= (fx2-fx3)/(x2-x3);
    ec2= (bi-ec)/(xo-x2);
    ec3= (ec-ec1)/(xi-x3);
    b3=(b2-ec3)/(xo-x3);
    ecua= bo+(bi*(p-xo))+(b2*(p-xi)*(p-xo))+(b3*(p-xo)*(p-xi)*(p-x2));
    resultado2.setText(String.valueOf(ecua));
    }
    
    
   
    
    
}//GEN-LAST:event_jButton1ActionPerformed

