private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
// TODO add your handling code here:
    int ang= Integer.parseInt(angulos.getText().toString());
    int i=0,j=0;
    
    if (u.isSelected())
    {
        tablon.setValueAt(" 40 - 45 ", 0, 5);
        tablon.setValueAt(" 46 - 50 ", 1, 5);
    for(i=0;i<=1;i++)
    {
      for (j=0;j<=4;j++)
      {
        lo.leemos(ang++);
        tablon.setValueAt(lo.tag(ang), i, j);
      }
    }
    }
    
    if (c.isSelected())
    {
        tablon.setValueAt(" 65-85 ", 0, 5);
       
    for(i=0;i<=2;i++)
    {
      for (j=0;j<=4;j++)
      {
        if(i==0)
        {
        lo.leemos(ang+=5);
        tablon.setValueAt(lo.tag(ang), i, j);
        }
        else
        {
        tablon.setValueAt("", i, j);
        }
        }
    }
    }
     
     if (d.isSelected())
    {
        tablon.setValueAt(" 0-30 ", 0, 5);
        tablon.setValueAt(" 31-60 ", 1, 5);
        tablon.setValueAt(" 61-90 ", 2, 5);
    for(i=0;i<=2;i++)
    {
      for (j=0;j<=4;j++)
      {
        if(j<=2)
      {    
        lo.leemos(ang+=10);
        tablon.setValueAt(lo.tag(ang), i, j);
    }
        else{
         tablon.setValueAt("", i, j);
                 }
    }
    }
    }
    
}//GEN-LAST:event_jButton1ActionPerformed

