private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
// TODO add your handling code here:
    double ti=0;
    double a= Double.parseDouble(jTextField1.getText());
    double b= Double.parseDouble(jTextField2.getText());
    c49.orden(Double.parseDouble(jTextField1.getText()));
    c49.argumento(Double.parseDouble(jTextField2.getText()));
    for (int i=0; i<=200; i++)
    {
    
     c49.ka(i);
     
     tabla.setValueAt(i, i,0);
     tabla.setValueAt(a, i, 1);
     tabla.setValueAt(b, i, 2);
     tabla.setValueAt(String.valueOf(c49.bessel()), i, 3);
      if (c49.bessel() >= 100)
     {
      i=200; 
      
     }
  
    
    }
}//GEN-LAST:event_jButton1ActionPerformed

