private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton4ActionPerformed
int i,j;
for(i=0; i<4;i++)
{
    for(j=0;j<4;j++)
    {
      if(i==j)
      {
          cajona1.setText(cajona1.getText()+" "+tabla.getValueAt(i, j).toString());
      }
      else if(i>j)
      {
       cajona2.setText(cajona2.getText()+" "+tabla.getValueAt(i, j).toString());
      }
      else if(i<j)
      {
       cajona3.setText(cajona3.getText()+" "+tabla.getValueAt(i, j).toString());
      }   
    }     
  }


// TODO add your handling code here:
}//GEN-LAST:event_jButton4ActionPerformed
