    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
      try{
        Trapecio trapecio= new Trapecio();
        trapecio.CalcularIntegral(vfuncion.getText(),vv1.getText(),vv2.getText());
        ressimpson.setText(trapecio.getArea());
      }catch(Exception e){
          JOptionPane.showMessageDialog(ressimpson, "Ocurrio un error al procesar su peticion.\nVerifique que su ecuacion este escrita correctamente.");
      }
      
    }//GEN-LAST:event_jButton1ActionPerformed

