private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
// TODO add your handling code here:
    int ax, bx, cx, co;
    double rais, x1,x2,res1, res2;
    
    ax= Integer.parseInt(a.getText());
    bx= Integer.parseInt(b.getText());
    cx= Integer.parseInt(c.getText());
    
    
    co= ((bx*bx)-(4*ax*cx));
    if(co<0)
    {
     res.setText("La ecuacion tiene un Numero complejo en la raiz");  
    }
    else
    {
    x1= ((-1*bx)+Math.sqrt(co))/(2*ax);
    x2= ((-1*bx)-Math.sqrt(co))/(2*ax);       
    
    res1= (ax* Math.pow(x1, 2))+(bx*x1)+cx;
    res2= (ax* Math.pow(x2, 2))+(bx*x2)+cx; 
    if(res1==0)
    {
    res.setText(String.valueOf(co + "  con opcion positiva"));
    }
    else
    {
    if(res2==0)
    {
    res.setText(String.valueOf(co+ "  con opcion negativa"));
    }
    else{
    res.setText("Lo siento esta ecuacion n/ no tiene un resultado exacto");
    }
    }
    }
    
    
}//GEN-LAST:event_jButton1ActionPerformed

