private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
// TODO add your handling code here:
      String p=" ";
        for(int j=0;j<=99;j++){
        for(int k=0;k<=4;k++){
        tabla.setValueAt(p, j, k);
        }
        }
        double x4=Float.parseFloat(c1.getText());
        double x3=Float.parseFloat(c2.getText());
        double x2=Float.parseFloat(c3.getText());
        double x1=Float.parseFloat(c4.getText());
        double c=Float.parseFloat(c5.getText());
        double xl=Float.parseFloat(c6.getText());
        double xu=Float.parseFloat(c7.getText());
        double err=(Float.parseFloat(c8.getText()));
        double v=100;
        
        double fxm;
        double xm;
                
        for(int j=0;err<=v;j++){
        
        
        xm=(xl+xu)/2;
        fxm=(x4*(Math.pow(xm,4)))+(x3*(Math.pow(xm,3)))+(x2*(Math.pow(xm,2)))+(x1*xm)+(c);
        
        tabla.setValueAt(j+1, j, 0);
        tabla.setValueAt(xl, j, 1);
        tabla.setValueAt(xu, j, 2);
        tabla.setValueAt(xm, j, 3);
       
        
        if(j==0){v=((xm-xu)/xm)*100;}
        
        if(j>=1){double xmant=Double.parseDouble(tabla.getValueAt(j-1, 3).toString());
        v=((xm-xmant)/xm)*100;}
        
        if(v<=0){
        v=v*-1;
        }
        
        tabla.setValueAt(v, j, 4);
        
          if(fxm<0){
            xl=xm;
          }
          else
          {
          if(fxm>0){
            xu=xm;
          }
          }
        }
      
}//GEN-LAST:event_jButton1ActionPerformed

