private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
// TODO add your handling code here:
       String z=" ";
        for(int j=0;j<=99;j++){
        for(int i=0;i<=4;i++){
        tabla.setValueAt(z, j, i);
        }
        }
        
        double x4=Float.parseFloat(c1.getText());
        double x3=Float.parseFloat(c2.getText());
        double x2=Float.parseFloat(c3.getText());
        double x1=Float.parseFloat(c4.getText());
        double c=Float.parseFloat(c5.getText());
        double xl=Float.parseFloat(c6.getText());
        double xu=Float.parseFloat(c7.getText());
        double err=(Float.parseFloat(c8.getText()));
        double e=1000;
        double fxl;
        double fxu;
        double fxm;
        double xm;

        for(int j=0;e>=err;j++){

            fxl=(x4*Math.pow(xl,4))+(x3*(Math.pow(xl,3)))+(x2*(Math.pow(xl,2)))+(x1*xl)+(c);
            fxu=(x4*(Math.pow(xu,4)))+(x3*(Math.pow(xu,3)))+(x2*(Math.pow(xu,2)))+(x1*xu)+(c);
            xm=((xu*fxl)-(xl*fxu))/((fxl)-(fxu));
            fxm=(x4*(Math.pow(xm,4)))+(x3*(Math.pow(xm,3)))+(x2*(Math.pow(xm,2)))+(x1*xm)+(c);
            

            tabla.setValueAt(j+1, j, 0);
            tabla.setValueAt(xl, j, 1);
            tabla.setValueAt(xu, j, 2);
            tabla.setValueAt(xm, j, 3);
           

            if(j==0){
                e=((xm-xu)/xm)*100;

            }

            if(j>=1){
                float xmant=Float.parseFloat(tabla.getValueAt(j-1, 3).toString());
                e=((xm-xmant)/xm)*100;

            }

            if(e<=0){
                e=e*-1;
            }

            tabla.setValueAt(e, j, 4);

            if(fxm<0){
                xl=xm;
            }
            if(fxm>0){
                xu=xm;
            }
        }
        
       
}//GEN-LAST:event_jButton1ActionPerformed

