    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        try {
            Simpn f = new Simpn();
            f.CalcularSimpson(vfunb.getText(), lmeb.getText(), lmab.getText(), segb.getText());
            resb.setText(f.getarea());
        } catch (Exception e) {
            JOptionPane.showMessageDialog(rootPane, "Ocurrio un error.\nVerifique que su ecuacion sea correcta y no deje nigun campo vacio");
        }
    }//GEN-LAST:event_jButton1ActionPerformed

