private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton4ActionPerformed
// TODO add your handling code here:
 // new Ticket().setVisible(true);
  int u=0;
  String o, p,q;
  
  for(u=0; u<suma;u++)
  {
   o = String.valueOf(tablamientras.getValueAt(u, 0));
   p =  String.valueOf(tablamientras.getValueAt(u, 1));
   q =  String.valueOf(tablamientras.getValueAt(u, 2));
  art.agarra(o,p,q);
 //ticket1.setText(suma+"\n\n");
  ticket1.setValueAt(p+" "+art.renta(), u, 0);
  }
    
}//GEN-LAST:event_jButton4ActionPerformed

